def crate_all():
    for cur_vid in videos:
        if not os.path.exists(cur_vid): os.makedirs(cur_vid)

        if 'Bunny' in cur_vid:
            vid_names = Bunny
            time_t_sub = Bunny_t_sub
            reference_video = "BigBuckBunny-2160.avi"
        if 'ElFuente' in cur_vid:
            vid_names = ElFuente
            time_t_sub = ElFuente_t_sub
            reference_video = "ElFuente-2160.avi"

        if 'Meridian' in cur_vid:
            vid_names = Meridian
            time_t_sub = Meridian_t_sub
            reference_video = "Meridian-2160.avi"

        if 'TearsOfSteel' in cur_vid:
            vid_names = TearsOfSteel
            time_t_sub = TearsOfSteel_t_sub
            reference_video = "TearsOfSteel-2160.avi"

        for vid in vid_names:
            job_count = 0
            for crf in crfs:
                for cur in ['fix', 'var']:
                    for max_dur in max_durs[cur][cur_vid]:
                        if 'var' in cur:
                            min_length = 0
                            target_seg_length = 0
                        else:
                            min_length = max_dur
                            target_seg_length = max_dur
                        job = {                                 \
                            "video": vid,                       \
                            "reference_video": reference_video,             \
                            "crf": crf,                       \
                            "min_length": min_length,                    \
                            "max_length": max_dur,              \
                            "target_seg_length": target_seg_length,\
                            "encoder": "x264",                  \
                        }
                        if 'var' in cur:
                            job["timestamps"] = time_t_sub[max_dur]
                        vid_name = os.path.splitext(vid)[0]

                        with open('{cur_vid}/{vid_name}_crf_{crf}_maxdur_{max_dur}_{cur}_job{job_count}.txt'\
                                .format(\
                                    cur_vid=cur_vid, \
                                    vid_name=vid_name, \
                                    crf=crf, \
                                    max_dur=str(max_dur).replace('.','c'), \
                                    job_count=job_count, \
                                    cur=cur \
                                    ), 'w') as fp:
                            json.dump(job, fp)
                        job_count += 1

